import React, { useState, useRef } from "react";
import "./App.css";
import "./Dashboard.css";
import Candidates from "./Candidates";
import Interviewers from "./Interviewers";
import Reports from "./Reports";
import Settings from "./Settings";
import AnimatedButton from "./AnimatedButton"; // Import the AnimatedButton
import './Loading.css'; // Import the loading styles

function App() {
  const [activeTab, setActiveTab] = useState("home");
  const [loading, setLoading] = useState(false); // Loading state
  const navRef = useRef(null);

  const renderContent = () => {
    switch (activeTab) {
      case "home":
        return (
          <div key="home" className="content">
            <h1>Upcoming Interviews</h1>
            <p>Details of upcoming interviews...</p>
          </div>
        );
      case "interviewers":
        return (
          <div>
            <Interviewers />
            <AnimatedButton text="Add Interviewers" onClick={() => alert("Add Interviewers Clicked!")} />
          </div>
        );
      case "candidates":
        return (
          <div>
            <Candidates />
            <AnimatedButton text="Add Candidates" onClick={() => alert("Add Candidates Clicked!")} />
          </div>
        );
      case "reports":
        return <Reports />;
      case "settings":
        return <Settings />;
      default:
        return null;
    }
  };

  const handleTabClick = (tab) => {
    setLoading(true); // Start loading
    setTimeout(() => {
      setActiveTab(tab);
      setLoading(false); // End loading after 500ms
    }, 500); // Simulate loading delay
  };

  return (
    <div className="app">
      {/* Dashboard Title */}
      <h1 className="dashboard-title">Dashboard</h1>

      <nav className="navbar">
        <ul ref={navRef}>
          {["home", "interviewers", "candidates", "reports", "settings"].map((tab) => (
            <li
              key={tab}
              className={activeTab === tab ? "active" : ""}
              onClick={() => handleTabClick(tab)} // Call handleTabClick on click
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1)}
            </li>
          ))}
        </ul>
      </nav>

      <main>
        {loading ? (
          <div className="loading">Loading...</div> // Show loading message
        ) : (
          renderContent() // Render content if not loading
        )}
      </main>
    </div>
  );
}

export default App;
