import React, { useState, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import "./App.css";
import "./Dashboard.css";
import Candidates from "./Candidates";
import Interviewers from "./Interviewers";
import Reports from "./Reports";
import Settings from "./Settings";
import AnimatedButton from "./AnimatedButton"; // Import the AnimatedButton

function App() {
  const [activeTab, setActiveTab] = useState("home");
  const navRef = useRef(null);

  const renderContent = () => {
    switch (activeTab) {
      case "home":
        return (
          <div key="home" className="content">
            <h1>Upcoming Interviews</h1>
            <p>Details of upcoming interviews...</p>
          </div>
        );
      case "interviewers":
        return (
          <div key="interviewers">
            <Interviewers />
            <AnimatedButton text="Add Interviewers" onClick={() => alert("Add Interviewers Clicked!")} />
          </div>
        );
      case "candidates":
        return (
          <div key="candidates">
            <Candidates />
            <AnimatedButton text="Add Candidates" onClick={() => alert("Add Candidates Clicked!")} />
          </div>
        );
      case "reports":
        return <div key="reports"><Reports /></div>;
      case "settings":
        return <div key="settings"><Settings /></div>;
      default:
        return null;
    }
  };

  const pageVariants = {
    initial: {
      opacity: 0,
      x: -50,
    },
    enter: {
      opacity: 1,
      x: 0,
      transition: {
        type: "spring",
        stiffness: 300,
        damping: 30,
      },
    },
    exit: {
      opacity: 0,
      x: 50,
      transition: {
        duration: 0.2,
      },
    },
  };

  return (
    <div className="app">
      {/* Dashboard Title */}
      <h1 className="dashboard-title">Dashboard</h1>

      <nav className="navbar">
        <ul ref={navRef}>
          {["home", "interviewers", "candidates", "reports", "settings"].map((tab) => (
            <li
              key={tab}
              className={activeTab === tab ? "active" : ""}
              onClick={() => {
                setActiveTab(tab);
              }}
            >
              {tab.charAt(0).toUpperCase() + tab.slice(1)}
            </li>
          ))}
        </ul>
      </nav>

      <main>
        <AnimatePresence mode="wait">
          <motion.div
            key={activeTab}
            variants={pageVariants}
            initial="initial"
            animate="enter"
            exit="exit"
          >
            {renderContent()}
          </motion.div>
        </AnimatePresence>
      </main>
    </div>
  );
}

export default App;
